// src/screens/Home.js

import React, { useState, useEffect } from 'react'
import { StyleSheet, View, Text, FlatList, TouchableOpacity, TextInput } from 'react-native'

function modifyActor(item){
	console(item);
	/*
	fetch("http://localhost:3000/person/"+item, {"method": "GET", mode: 'cors'}).then(response => response.json()).then(response => {
        setActor(response)
      }).catch(err => {console.log(err);}) onChangeText={text => item['Movies As Actor Actress'] = text}
	*/
}

function DetailActorsEdit(props) {
  const { route } = props
  const { navigation } = props
  const { item } = route.params
  var actor = {
	title: [],
	worked_as_actor: [],
	worked_as_director: [],
	worked_as_producer:[]
  };
  item['Title'] = item;
  return (
    <View style={styles.container}>
      <Text style={styles.text}>{params.item.Title}</Text>
      <View style={styles.card}>
        <Text style={styles.cardText}>Movies As Actor Actress:</Text>
		<TextInput style={styles.cardText} placeholder="Enter the movies he/she worked as actor separated by ," placeholderTextColor="#003f5c"/>
        <TextInput style={styles.cardText} placeholder="Enter the movies he/she worked as director separated by ," placeholderTextColor="#003f5c"/>
		<TextInput style={styles.cardText} placeholder="Enter the movies he/she worked as producer separated by ," placeholderTextColor="#003f5c"/>
		<TouchableOpacity style={{padding:0,margin:0}} onPress={() => modifyActor(item)}><Text style={styles.cardText}>Save</Text></TouchableOpacity>
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#ebebeb'
  },
  text: {
    color: '#101010',
    fontSize: 24,
    fontWeight: 'bold'
  },
  card: {
    width: 350,
    height: 400,
    borderRadius: 10,
    backgroundColor: '#101010',
    margin: 10,
    padding: 10,
    alignItems: 'left'
  },
  cardText: {
    fontSize: 18,
    color: '#ffd700',
    marginBottom: 5
  }
})

export default DetailActorsEdit
