// src/screens/Home.js

import React, { useState, useEffect } from 'react'
import { StyleSheet, View, Text } from 'react-native'

function DetailActors(props) {
  const { route } = props
  const { item } = route.params
  let [actor, setActor] = React.useState('')
  useEffect(() => {
	fetch("http://localhost:3000/person", {"method": "GET", mode: 'cors'}).then(response => response.json()).then(response => {
        setActor(response)
      }).catch(err => {console.log(err);})
  }, [])
  console.log(actor);
  return (
    <View style={styles.container}>
      <Text style={styles.text}>{item}</Text>
      <View style={styles.card}>
        <Text style={styles.cardText}>Movies As Actor Actress:</Text>
        <Text style={styles.cardText}>Movies As Director: {item['Movies As Director']}</Text>
		<Text style={styles.cardText}>Movies As Producer: {item['Movies As Producer']}</Text>
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#ebebeb'
  },
  text: {
    color: '#101010',
    fontSize: 24,
    fontWeight: 'bold'
  },
  card: {
    width: 350,
    height: 400,
    borderRadius: 10,
    backgroundColor: '#101010',
    margin: 10,
    padding: 10,
    alignItems: 'left'
  },
  cardText: {
    fontSize: 18,
    color: '#ffd700',
    marginBottom: 5
  }
})

export default DetailActors
