// src/screens/Home.js

import React, { useState, useEffect } from 'react'
import { StyleSheet, View, Text, TouchableOpacity, FlatList } from 'react-native'

function LoginSend(item){
	console.log(item);
	var data = {username: item['username'], password: item['password']};
	fetch("http://localhost:3000/login", {"method": "POST", body: actor_data, mode: 'cors'}).then(response => response.json()).then(response => {
        console.log(response);
		result_save = JSON.stringify(response);
    }).catch(err => {console.log(err);})
}

function Login(props) {
  const { navigation } = props
  var item = {};
  return (
    <View style={styles.container}>
		<TextInput style={styles.cardText} placeholder="Username" placeholderTextColor="#ccc" onChangeText={text => item['username'] = text}/>
        <TextInput style={styles.cardText} placeholder="Password" placeholderTextColor="#ccc" onChangeText={text => item['password'] = text}/>
		<TouchableOpacity style={styles.buttonContainer} onPress={() => LoginSend(item)}>Login</TouchableOpacity>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#ebebeb'
  },
  text: {
    color: '#101010',
    fontSize: 24,
    fontWeight: 'bold'
  },
  buttonContainer: {
    backgroundColor: '#222',
    borderRadius: 5,
    padding: 10,
    margin: 20
  },
  buttonText: {
    fontSize: 20,
    color: '#fff'
  }
})

export default Login
