// src/screens/Home.js

import React, { useState, useEffect } from 'react'
import { StyleSheet, View, Text, TouchableOpacity, FlatList } from 'react-native'

function fetchMovies(){
	fetch("http://localhost:3000/movie", {"method": "GET", mode: 'cors'}).then(response => response.json()).then(response => {
        return response;
      }).catch(err => {
        console.log(err);
  });
}

function Home(props) {
  const { navigation } = props
  let [movie, setMovie] = React.useState('')
  const character = {
	  name: 'Luke Skywalker',
	  home: 'Tatooine',
	  species: 'human'
	}
  useEffect(() => {
	setMovie(fetchMovies())
  }, [])
  return (
    <View style={styles.container}>
      <Text style={styles.text}>Home Screen</Text>
	  <TouchableOpacity
		  style={styles.buttonContainer}
		  onPress={() => navigation.navigate('Detail', { item: character })}>
		  <Text style={styles.buttonText}>Who is {character.name}?</Text>
	  </TouchableOpacity>
	  <FlatList data={movie} renderItem={({item}) => 
        <TouchableOpacity style={styles.item} key={item.Id} onPress={() => navigation.navigate('Detail')}><Text>{item.Title}</Text></TouchableOpacity>}/>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#ebebeb'
  },
  text: {
    color: '#101010',
    fontSize: 24,
    fontWeight: 'bold'
  },
  buttonContainer: {
    backgroundColor: '#222',
    borderRadius: 5,
    padding: 10,
    margin: 20
  },
  buttonText: {
    fontSize: 20,
    color: '#fff'
  }
})

export default Home
